import{_ as e,c as t,o as a,a as n}from"./app.7f0e30f4.js";const _=JSON.parse('{"title":"\u57FA\u672C\u6982\u5FF5","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u7FA4\u7EC4/\u9891\u9053 - Guild/Channel","slug":"\u7FA4\u7EC4-\u9891\u9053-guild-channel","link":"#\u7FA4\u7EC4-\u9891\u9053-guild-channel","children":[]},{"level":2,"title":"\u9002\u914D\u5668 - Adapter","slug":"\u9002\u914D\u5668-adapter","link":"#\u9002\u914D\u5668-adapter","children":[]},{"level":2,"title":"\u63D2\u4EF6 - Plugin","slug":"\u63D2\u4EF6-plugin","link":"#\u63D2\u4EF6-plugin","children":[]},{"level":2,"title":"\u4E2D\u95F4\u4EF6 - Middleware","slug":"\u4E2D\u95F4\u4EF6-middleware","link":"#\u4E2D\u95F4\u4EF6-middleware","children":[]}],"relativePath":"start/basic-concepts.md","lastUpdated":1670095480000}'),d={name:"start/basic-concepts.md"},l=n('<h1 id="\u57FA\u672C\u6982\u5FF5" tabindex="-1">\u57FA\u672C\u6982\u5FF5 <a class="header-anchor" href="#\u57FA\u672C\u6982\u5FF5" aria-hidden="true">#</a></h1><h2 id="\u7FA4\u7EC4-\u9891\u9053-guild-channel" tabindex="-1">\u7FA4\u7EC4/\u9891\u9053 - Guild/Channel <a class="header-anchor" href="#\u7FA4\u7EC4-\u9891\u9053-guild-channel" aria-hidden="true">#</a></h2><p>\u8003\u8651\u5230\u4F17\u591A\u804A\u5929\u5E73\u53F0\u5177\u6709\u4E0D\u7EDF\u4E00\u7684\u67B6\u6784\uFF08\u4F8B\u5982 Discord \u7684\u670D\u52A1\u5668\u3001\u9891\u9053\uFF0CQQ \u7684\u7FA4\u804A\uFF09\uFF0CFlandre \u5C06\u5176\u62BD\u8C61\u6210\u4E24\u5C42\uFF1AGuild \u548C Channel\u3002Guild \u6CDB\u6307\u5927\u7684\u7FA4\u7EC4\u6784\u6210\uFF0C\u5176\u4E2D\u6709\u8BB8\u591A\u7684\u5206\u533A\u548C\u6210\u5458\uFF1BChannel \u6307\u5404\u79CD\u4E0D\u540C\u529F\u80FD\u7684\u5206\u533A\uFF0C\u4E5F\u662F\u7528\u6237\u4EEC\u5177\u4F53\u804A\u5929\u7684\u5730\u65B9\u3002</p><p>\u5BF9\u4E8E\u62E5\u6709 Discord \u4F7F\u7528\u7ECF\u9A8C\u7684\u7528\u6237\u6765\u8BF4\u8FD9\u79CD\u7ED3\u6784\u5F88\u597D\u7406\u89E3\uFF1B\u4F46\u5728 QQ \u8FD9\u6837\u53EA\u7528\u4E00\u5C42\u7FA4\u804A\u5212\u5206\u7528\u6237\u7684\u5E73\u53F0\uFF0C\u6211\u4EEC\u5B9A\u4E49 Guild \u4E3A\u7FA4\u804A\u7684\u57FA\u672C\u62BD\u8C61\uFF0C\u662F\u7FA4\u6210\u5458\u7684\u4E00\u4E2A\u96C6\u5408\uFF0CChannel \u4E3A\u7FA4\u6210\u5458\u5177\u4F53\u804A\u5929\u7684\u73AF\u5883\u3002\u4F8B\u5982\u53D1\u9001\u7FA4\u804A\u6D88\u606F\u4F7F\u7528 <code>SendChannelMessage</code>\uFF0C\u800C\u83B7\u53D6\u7FA4\u6210\u5458\u4F7F\u7528 <code>GetGuildMember</code>\u3002</p><p>\u51FA\u4E8E\u65B9\u4FBF\u76EE\u7684\uFF0C\u6211\u4EEC\u5728\u6587\u6863\u4E2D\u4F1A\u5C06 Guild \u8BD1\u4E3A <strong>\u7FA4\u7EC4</strong>\uFF0CChannel \u8BD1\u4E3A <strong>\u9891\u9053</strong>\u3002\u5728\u6587\u6863\u4E2D\uFF0C\u9664\u975E\u662F\u7279\u5B9A\u5E73\u53F0\u76F8\u5173\u7684\u573A\u666F\uFF0C\u6211\u4EEC\u90FD\u4F1A\u7528 <strong>\u7FA4\u7EC4/\u9891\u9053</strong> \u4EE3\u66FF Guild/Channel\u3002</p><p>\u4E3A\u4E86\u80FD\u66F4\u597D\u5730\u7406\u89E3\uFF0C\u5728\u8FD9\u91CC\u6574\u7406\u4E86\u4E00\u4E2A\u5BF9\u7167\u8868\uFF0C\u5BF9\u5E94\u4E0D\u540C\u5E73\u53F0\u4F7F\u7528\u7684\u4E0D\u540C\u672F\u8BED\uFF1A</p><table><thead><tr><th style="text-align:center;">Flandre</th><th style="text-align:center;">OneBot (QQ)</th><th style="text-align:center;">Discord</th><th style="text-align:center;">QQ \u9891\u9053</th></tr></thead><tbody><tr><td style="text-align:center;"><strong>\u7FA4\u7EC4/Guild</strong></td><td style="text-align:center;">\u7FA4\u804A\u6210\u5458\u7684\u4E00\u4E2A\u62BD\u8C61\u96C6\u5408</td><td style="text-align:center;">\u670D\u52A1\u5668 (Server)</td><td style="text-align:center;">\u9891\u9053</td></tr><tr><td style="text-align:center;"><strong>\u9891\u9053/Channel</strong></td><td style="text-align:center;">\u7FA4\u6210\u5458\u7684\u5177\u4F53\u804A\u5929\u73AF\u5883</td><td style="text-align:center;">\u9891\u9053</td><td style="text-align:center;">\u5B50\u9891\u9053</td></tr></tbody></table><h2 id="\u9002\u914D\u5668-adapter" tabindex="-1">\u9002\u914D\u5668 - Adapter <a class="header-anchor" href="#\u9002\u914D\u5668-adapter" aria-hidden="true">#</a></h2><p>\u5BF9\u4E8E\u8DE8\u5E73\u53F0\u7684\u5B9E\u73B0\u65B9\u6848\uFF0CFlandre \u53C2\u8003\u8BF8\u591A\u540C\u7C7B\u9879\u76EE\uFF0C\u4F7F\u7528\u4E86\u9002\u914D\u5668 (Adapter) \u6A21\u5F0F\u3002\u5728\u9002\u914D\u5668\u6A21\u5F0F\u4E2D\uFF0C\u6838\u5FC3\u7EC4\u4EF6 (Flandre.Core) \u4E0D\u4F1A\u53D1\u751F\u53D8\u5316\uFF0C\u5BF9\u4E8E\u4E0D\u540C\u5E73\u53F0\u53EA\u9700\u8981\u7F16\u5199\u5BF9\u5E94\u7684\u9002\u914D\u5668\uFF0C\u5145\u5F53\u548C Core \u901A\u4FE1\u7684\u6865\u6881\u3002</p><h2 id="\u63D2\u4EF6-plugin" tabindex="-1">\u63D2\u4EF6 - Plugin <a class="header-anchor" href="#\u63D2\u4EF6-plugin" aria-hidden="true">#</a></h2><p>\u63D2\u4EF6\u662F\u7531\u5F00\u53D1\u8005\u81EA\u884C\u7F16\u5199\u7684\u529F\u80FD\u6A21\u5757\uFF0C\u5305\u542B\u4E86 Bot \u7684\u5404\u79CD\u884C\u4E3A\u903B\u8F91\uFF0C\u5982\u6307\u4EE4\u3001\u4E8B\u4EF6\u5904\u7406\u90FD\u662F\u5305\u542B\u5728\u4E00\u4E2A\u4E2A\u6A21\u5757\u91CC\u7684\u3002</p><h2 id="\u4E2D\u95F4\u4EF6-middleware" tabindex="-1">\u4E2D\u95F4\u4EF6 - Middleware <a class="header-anchor" href="#\u4E2D\u95F4\u4EF6-middleware" aria-hidden="true">#</a></h2><p>\u4E2D\u95F4\u4EF6\u662F\u6D88\u606F\u8FDB\u51FA\u7684\u7BA1\u9053\u3002Flandre \u7684\u4E2D\u95F4\u4EF6\u8BBE\u8BA1\u53C2\u8003\u4E86\u6D0B\u8471\u6A21\u578B\uFF0C\u4FBF\u4E8E\u5BF9\u6D88\u606F\u8FDB\u884C\u5168\u9762\u7684\u63A7\u5236\u3002</p>',13),r=[l];function i(s,c,h,o,p,g){return a(),t("div",null,r)}const x=e(d,[["render",i]]);export{_ as __pageData,x as default};
